INSERT INTO `base_notice` VALUES ('23', '2015年天津关于药品集中采购项目企业 网上注册和现场复审截止时间的提示', '&lt;p style=&quot;TEXT-INDENT: 32pt&quot;&gt;&lt;span style=&quot;font-family:宋体, SimSun;font-size:18px&quot;&gt;2015年天津市药品集中采购项目（项目编号：TJMPC-2015-YP-01）企业网上注册系统将于2015年9月21日12:00，在相关部门监督下关闭，请尚未注册及未通过初审的企业尽快提交，及时完成网上注册工作。 &lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;TEXT-INDENT: 32pt&quot;&gt;&lt;span style=&quot;font-family:宋体, SimSun;font-size:18px&quot;&gt;现场复审工作计划于2015年9月30日11:30完成，截至9月15日18:00，已有一千多家企业完成现场复审并成功换领或领取UKEY（数字证书版）。请已参加天津市医疗机构常用低价药品集中采购项目注册并成功领取USB-key但尚未换领UKEY（数字证书版）的企业，尽快通过网上预约后到中心换领UKEY（数字证书版）；请已通过初审，未进行现场复审的新注册企业，合理安排时间，尽快进行网上预约后到中心完成现场复审工作。 &lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;TEXT-INDENT: 32pt&quot;&gt;&lt;span style=&quot;font-family:宋体, SimSun;font-size:18px&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;TEXT-ALIGN: right; TEXT-INDENT: 32pt&quot; align=&quot;right&quot;&gt;&lt;span style=&quot;font-family:宋体, SimSun;font-size:18px&quot;&gt;天津市医药采购中心 &lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;TEXT-ALIGN: right; TEXT-INDENT: 32pt&quot; align=&quot;right&quot;&gt;&lt;span style=&quot;font-family:宋体, SimSun;font-size:18px&quot;&gt;2015年9月16日&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '1442461009');
