INSERT INTO `base_notice` VALUES ('66', '上海市2015年第三批常用低价药拟挂网采购公示', '&lt;p style=&quot;;list-style: none;color: rgb(0, 0, 0);white-space: normal;text-align: justify;font-stretch: normal;font-size: 14px;line-height: normal;font-family: Calibri, sans-serif;background-color: rgb(252, 252, 252)&quot;&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;各有关药品生产企业：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;list-style: none;text-indent: 24px;color: rgb(0, 0, 0);font-family: Simsun;font-size: 12px;line-height: 24px;white-space: normal;background-color: rgb(252, 252, 252)&quot;&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;现将上海市&lt;span&gt;2015&lt;/span&gt;年第三批常用低价药拟挂网采购药品予以公示（详见附件），具体挂网采购时间另行公布。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;list-style: none;text-indent: 24px;color: rgb(0, 0, 0);font-family: Simsun;font-size: 12px;line-height: 24px;white-space: normal;background-color: rgb(252, 252, 252)&quot;&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;公示时间：&lt;span&gt;2015&lt;/span&gt;年&lt;span&gt;11&lt;/span&gt;月&lt;span&gt;16&lt;/span&gt;日至&lt;span&gt;2015&lt;/span&gt;年&lt;span&gt;11&lt;/span&gt;月&lt;span&gt;23&lt;/span&gt;日&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;list-style: none;text-indent: 24px;color: rgb(0, 0, 0);font-family: Simsun;font-size: 12px;line-height: 24px;white-space: normal;background-color: rgb(252, 252, 252)&quot;&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-family: 仿宋;font-size: 21px&quot;&gt;公示期间如有异议，请于&lt;span&gt;11&lt;/span&gt;月&lt;span&gt;23&lt;/span&gt;日前通过上海市阳光医药采购网（&lt;span&gt;www.smpaa.cn&lt;/span&gt;）的《上海市医药采购基础信息登记系统》&lt;span&gt;“&lt;/span&gt;信息公示&lt;span&gt;”&lt;/span&gt;模块进行情况反映。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;;list-style