INSERT INTO `base_notice` VALUES ('96', '关于及时确认报名信息的通知', '&lt;p&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: 仿宋; COLOR: black&quot;&gt;各有关药品生产企业：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: 仿宋; COLOR: black&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; \r\n2015&lt;/span&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: 仿宋; COLOR: black&quot;&gt;年度江西省公立医院药品集中采购生产企业网上确认报名信息将于&lt;/span&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: &amp;quot;微软雅黑&amp;quot;, sans-serif; COLOR: red&quot;&gt;12&lt;/span&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: &amp;quot;微软雅黑&amp;quot;, sans-serif; COLOR: red&quot;&gt;月10日（周四）17：30&lt;/span&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: 仿宋; COLOR: black&quot;&gt;截止，目前还有部分企业的报名信息“待确认”或者“确认不通过”，请在规定时间内登录江西省医药采购服务平台的“非低价药报名系统”进行报名信息核对和确认，逾期不确认或者未“确认通过”的，一律视为自动放弃本次药品集中采购。&lt;/span&gt;&lt;span style=&quot;FONT-SIZE: 9pt; FONT-FAMILY: &amp;quot;微软雅黑&amp;quot;, sans-serif; COLOR: black&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;FONT-SIZE: 9pt; FONT-FAMILY: &amp;quot;微软雅黑&amp;quot;, sans-serif; COLOR: black&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;&lt;span style=&quot;FONT-SIZE: 9pt; FONT-FAMILY: &amp;quot;微软雅黑&amp;quot;, sans-serif; COLOR: black&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;FONT-SIZE: 9pt; FONT-FAMILY: &amp;quot;微软雅黑&amp;quot;, sans-serif; COLOR: black&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p align=&quot;right&quot;&gt;&lt;span style=&quot;FONT-SIZE: 16pt; FONT-FAMILY: 仿宋; COLOR: black&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nb